import React from "react";
import { Text, View, FlatList, Image, Dimensions, StyleSheet } from "react-native";
import { useSelector } from 'react-redux';
import DropDownPicker from 'react-native-dropdown-picker';
import Icon from 'react-native-vector-icons/AntDesign';
import NumericInput from 'react-native-numeric-input'

const cart = () => {

    const Cart = useSelector(state => state.Cart.CartItems)

    return (
        <View style={{ alignItems: 'center', justifyContent: 'center', backgroundColor: '#fff' }}>
            {
                Cart !== null ? (
                    <FlatList
                        data={Cart}
                        renderItem={({ item }) => (
                            <View style={styles.outer}>
                                <Image source={{ uri: item.image }} style={{ width: '20%', height: '100%' }} resizeMode={'contain'} />
                                <NumericInput
                                    value={item.Qty}
                                    onChange={()=>{}}
                                    onLimitReached={(isMax, msg) => console.log(isMax, msg)}
                                    totalWidth={'30%'}
                                    totalHeight={40}
                                    iconSize={25}
                                    step={1}
                                    minValue={1}
                                    maxValue={5}
                                    textColor='#B0228C'
                                    
                                    />
                                <Text style={styles.text}>Rs {item.price}</Text>
                                <Icon.Button name='delete' size={20} color='crimson' backgroundColor='#fff' onPress={() => { }}
                                />
                            </View>

                        )}
                        keyExtractor={(item) => item._id}

                    />
                ) : <Text>no cart items</Text>
            }


        </View>
    )
}

export default cart;

const styles = StyleSheet.create({
    outer: {
        flexDirection: 'row',
        alignItems: 'center',
        width: Dimensions.get('window').width,
        height: 80,
        backgroundColor: '#fff',
        justifyContent: 'space-between',
        marginBottom: 5,


    },
    inner: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',


    },
    image: {
        width: '100%',
        height: '90%',
        flex: 3



    },
    text: {
        fontSize: 15,
        fontWeight: 'bold',
        alignSelf: 'center',
        color: 'grey',

    }

})
