import 'react-native-gesture-handler';
import React from 'react';
import { StyleSheet, Text, View,Button } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createDrawerNavigator } from '@react-navigation/drawer';
import { createStackNavigator } from '@react-navigation/stack';
import home from './src/home';
import cart from './src/cart';
import Icon from 'react-native-vector-icons/Entypo';
import { Provider } from "react-redux";
import store from "./store";
import Details from './src/Details';

const Drawer = createDrawerNavigator();

const Stack = createStackNavigator();

const Homescreen=({navigation})=>(
  <Stack.Navigator screenOptions={{
          headerStyle: {
            backgroundColor: '#213970',
          },
          headerTintColor: '#fff',
          headerTitleStyle: {
            fontWeight: 'bold',
          },
  }}>
      <Stack.Screen name="home" component={home} options={{title:'Shopping Cart',
       headerLeft:()=>(
         <Icon.Button name='menu' size={25} color='#fff' backgroundColor='#213970' onPress={()=>
           navigation.openDrawer()
         }/>

       )

    }}/>
    <Stack.Screen name="Details" component={Details}/>
    </Stack.Navigator>
)

const Cartscreen=({navigation})=>(
  <Stack.Navigator screenOptions={{
          headerStyle: {
            backgroundColor: '#213970',
          },
          headerTintColor: '#fff',
          headerTitleStyle: {
            fontWeight: 'bold',
          },
  }}>
      <Stack.Screen name="cart" component={cart} options={{title:'Cart Items',
       headerLeft:()=>(
         <Icon.Button name='menu' size={25} color='#fff' backgroundColor='#213970' onPress={()=>
           navigation.openDrawer()
         }/>

       )

    }}/>
    </Stack.Navigator>
)



const App=()=>{
  return(
    <Provider store={store}>
    <NavigationContainer>
      <Drawer.Navigator initialRouteName="Home">
        <Drawer.Screen name="home" component={Homescreen} options={{
          drawerIcon: ({focused, size}) => (
            <Icon
              name="home"
              size={size}
              color={focused ? 'cornflowerblue' : 'grey'}
            />
          )
        }}/>
        <Drawer.Screen name="Cart" component={Cartscreen} options={{
          drawerIcon: ({focused, size}) => (
            <Icon
              name="shopping-cart"
              size={size}
              color={focused ? 'cornflowerblue' : 'grey'}
            />
          )
        }}/>
      </Drawer.Navigator>

    </NavigationContainer>
    </Provider>
  )
}


export default App;
