import { item_success } from "../actions/types";

const initialState={
   value:false,
   items:null
}


export default function(state=initialState,action){
 
    const {type,payload}=action;

    switch(type){
        case item_success:
            return{
                ...state,
                ...payload,
                value:true,
                items:payload
              
            }
        default:
            return{
                ...state
            }
    }
}
