import React from "react";
import {Text,View,FlatList,Image,Dimensions,StyleSheet} from "react-native";
import {useSelector} from 'react-redux';
import DropDownPicker from 'react-native-dropdown-picker';
import Icon from 'react-native-vector-icons/AntDesign';

const cart=()=>{

    const Cart=useSelector(state => state.Cart.CartItems)

    return(
        <View style={{alignItems:'center',justifyContent:'center'}}>
            {
                Cart!==null ? (
                    <FlatList
        data={Cart}
        renderItem={({item})=>(
            <View style={styles.outer}>
            <Image source={{ uri: item.image }} style={{width:'30%',height:'100%',margin:10}} resizeMode={'contain'} />
            <View style={{ alignSelf: 'center', width:'10%',flexDirection: 'row', justifyContent: 'center' }}>
                        <DropDownPicker
                            items={[
                                { label: '1', value: 1 },
                                { label: '2', value: 2 },
                                { label: '3', value: 3 },
                                { label: '4', value: 4 },

                            ]}
                            defaultValue={item.Qty}
                            containerStyle={{ height: 40, width: '100%', alignSelf: 'center' }}
                            style={{ backgroundColor: 'lightsteelblue' }}
                            itemStyle={{
                                justifyContent: 'center'
                            }}
                            dropDownStyle={{ backgroundColor: '#fafafa' }}
                            onChangeItem={(event) => {
                                setcart({
                                    ...cart,
                                    Qty: event.value
                                })
                            }}
                        /></View>
                         <Text>Rs {item.price}</Text>
                         <Icon.Button name='delete' size={25} color='crimson' backgroundColor='#fff' onPress={()=>{}}
         />
            </View>

        )}
        keyExtractor={(item) => item._id}
       
      />
                ): <Text>no cart items</Text>
            }
            

        </View>
    )
}

export default cart;

const styles = StyleSheet.create({
    outer: {
        flexDirection: 'row',
        alignItems: 'center',
        width: Dimensions.get('window').width,
        height: 100,
        backgroundColor: '#fff',
        justifyContent: 'center',
        marginBottom: 5,
        

    },
    inner: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',


    },
    image: {
        width: '100%',
        height: '90%',
        flex: 3



    },
    text: {
        fontSize: 15,


        alignSelf: 'center',
        color: 'grey'
    }

})
