import React from "react";

import {Text,View,FlatList,Image,Dimensions,StyleSheet} from "react-native";
import {useSelector} from 'react-redux';

const cart=()=>{

    const Cart=useSelector(state => state.Cart.CartItems)

    return(
        <View>
            {
                Cart!==null ? (
                    <FlatList
        data={Cart}
        renderItem={({item})=>(
            <View style={styles.outer}>
            <Image source={{ uri: item.image }} style={{width:'100%',height:'100%'}} resizeMode={'contain'} />
            <Text>Rs {item.price}</Text>
            </View>

        )}
        keyExtractor={(item) => item._id}
       
      />
                ): <Text>no cart items</Text>
            }
            

        </View>
    )
}

export default cart;

const styles = StyleSheet.create({
    outer: {
        flexDirection: 'row',
        alignItems: 'center',
        width: Dimensions.get('window').width,
        height: '10%',
        backgroundColor: '#fff',
        justifyContent: 'center',
        marginBottom: 5,
        marginTop: 5

    },
    inner: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',


    },
    image: {
        width: '100%',
        height: '90%',
        flex: 3



    },
    text: {
        fontSize: 15,


        alignSelf: 'center',
        color: 'grey'
    }

})
