import { cart_add } from "../actions/types";

const initialState = {
    CartItems: [],
    Total: 0
}


export default function (state = initialState, action) {

    const { type, payload } = action


    switch (type) {
        case cart_add:
            const item = payload

            const existItem = state.CartItems.find((x) => x.name === item.name)

            if (existItem) {
                return {
                    ...state,
                CartItems:state.CartItems.map((x)=>{
                    if(x.name===item.name){
                        return item
                    }
                    else{
                        return x
                    }
                }) 
                }
            } else {
                return {
                    ...state,
                    CartItems: [...state.CartItems, item],
                }
            }





        default:
            return {
                ...state
            }
    }
}
