import React, { useState, useEffect } from "react";
import { SafeAreaView, View, FlatList, StyleSheet, Text, StatusBar, Image, Dimensions, Button, TouchableOpacity } from 'react-native';
import { connect, useSelector } from "react-redux";
import { Items } from '../actions/items';
import { Rating } from 'react-native-ratings';


const home = ({ Items, navigation }) => {

    const Item = useSelector(state => state.Item);


    const [item, setitem] = useState(null)

    useEffect(() => {

        Items()
        if (Item.value) {
            setitem(Item.items)
        }


    }, [Item.value])



    return (
        <View style={{ alignItems: 'center' }}>
            {
                item != null ? (
                    <FlatList
                        data={item}
                        renderItem={({ item }) => (
                            <TouchableOpacity onPress={() => {
                                navigation.navigate("Details", {
                                    Selected: item
                                })
                            }}>

                                <View style={styles.outer}>
                                    <Image source={{ uri: item.image }} style={styles.image} resizeMode={'contain'} />

                                    <View style={styles.inner}>
                                        <Text style={[styles.text, { fontWeight: 'bold' }]}>{item.name}</Text>


                                    </View>
                                    <Text>{item.description}</Text>
                                    <View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'center', margin: 10 }}>
                                        <Rating
                                            type='star'
                                            ratingCount={5}
                                            imageSize={18}
                                            onFinishRating={() => { }}
                                            startingValue={item.rating}
                                        />
                                        <Text style={{ color: 'lightskyblue', fontSize: 15, marginLeft: 10 }}>({item.count})</Text>
                                        <Text style={[styles.text, { color: 'brown', marginLeft: '15%', fontSize: 15 }]}>Rs {item.price}</Text>

                                    </View>
                                </View>
                            </TouchableOpacity>

                        )}
                        keyExtractor={item => item._id}
                    />

                ) : (
                        <Text>home screen</Text>
                    )
            }

        </View>
    )
}

const styles = StyleSheet.create({
    outer: {
        flexDirection: 'column',
        alignItems: 'center',
        width: Dimensions.get('window').width,
        height: 230,
        backgroundColor: '#fff',
        justifyContent: 'center',
        marginBottom: 5,
        marginTop: 5

    },
    inner: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',


    },
    image: {
        width: '100%',
        height: '90%',
        flex: 3



    },
    text: {
        fontSize: 15,


        alignSelf: 'center',
        color: 'grey'
    }

})

export default connect(null, { Items })(home);
